Ext.define('platform.tools.view.RightRejectWindow', {
    extend: 'Ext.window.Window',

    requires: [
        'Ext.toolbar.Toolbar',
        'Ext.button.Button',
        'Ext.form.Panel',
        'Ext.form.field.Hidden',
        'Ext.tree.Panel',
        'Ext.tree.View'
    ],

    height: 500,
    width: 450,
    title: '菜单互斥',
    modal: true,

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            dockedItems: [
                {
                    xtype: 'toolbar',
                    dock: 'bottom',
                    layout: {
                        type: 'hbox',
                        align: 'middle',
                        pack: 'end'
                    },
                    items: [
                        {
                            xtype: 'button',
                            iconCls: 'icon-save',
                            text: '保存',
                            listeners: {
                                click: {
                                    fn: me.onBtnSaveClick,
                                    scope: me
                                }
                            }
                        },
                        {
                            xtype: 'button',
                            iconCls: 'icon-cancel',
                            text: '取消',
                            listeners: {
                                click: {
                                    fn: me.onBtnCancelClick,
                                    scope: me
                                }
                            }
                        }
                    ]
                }
            ],
            items: [
                {
                    xtype: 'form',
                    border: false,
                    height: 442,
                    autoScroll: true,
                    header: false,
                    title: '互斥菜单',
                    items: [
                        {
                            xtype: 'hiddenfield',
                            anchor: '100%',
                            fieldLabel: 'Label',
                            name: 'refId'
                        },
                        {
                            xtype: 'hiddenfield',
                            anchor: '100%',
                            fieldLabel: 'Label',
                            name: 'rightIds'
                        },
                        {
                            xtype: 'treepanel',
                            border: false,
                            autoScroll: true,
                            header: false,
                            title: '树列表',
                            rootVisible: false,
                            viewConfig: {

                            },
                            listeners: {
                                afterrender: {
                                    fn: me.onTreepanelAfterRender,
                                    scope: me
                                },
                                itemclick: {
                                    fn: me.onTreepanelItemClick,
                                    scope: me
                                }
                            }
                        }
                    ],
                    listeners: {
                        afterrender: {
                            fn: me.onFormAfterRender,
                            scope: me
                        }
                    }
                }
            ]
        });

        me.callParent(arguments);
    },

    onBtnSaveClick: function(button, e, eOpts) {
         var me  = this;
        try{
             var rightIds = [];
            var items = me.treePanel.getSelectionModel().store.data.items;
            Ext.each(items, function()
                     {
                         var nd = this;
                         if(nd.data.checked&&
                            (nd.data.type==='URL'||nd.data.type==='COMPONENT'||nd.data.type==='BUTTON'))
                         {
                             rightIds.push(nd.data.id);
                         }
                     });
            me.form.getForm().findField('rightIds').setValue(rightIds);
            Common.formSubmit({
                url : ctxp+'/ptools/right/reject/save',
                form:me.form,
                callback : function(result)
                {
                    Ext.Msg.alert('信息提示', '保存成功！');
                	me.close();
            	}
        	});
        }catch(error){
            Common.show({title:'信息提示',html:error.toString()});
        }
    },

    onBtnCancelClick: function(button, e, eOpts) {
        var me = this;
        me.close();
    },

    onTreepanelAfterRender: function(component, eOpts) {
        this.treePanel = component;
    },

    onTreepanelItemClick: function(dataview, record, item, index, e, eOpts) {
        if(record.data.checked)
        {
            record.set('checked', false);
            Common.onTreePanelCheckChange(record,false,eOpts);
        }
        else
        {
            record.set('checked', true);
            Common.onTreePanelCheckChange(record,true,eOpts);
        }
    },

    onFormAfterRender: function(component, eOpts) {
        this.form = component;
    },

    loadForm: function(id) {
        var me = this;
        me.form.getForm().findField("refId").setValue(id);
                Common.ajax({
                    component : me.treePanel,
                    message : '正在加载......',
                    url :ctxp + '/ptools/right/reject/list?id='+id,
                    callback : function(result){
                        var myStore = Ext.create("Ext.data.TreeStore",{
                                defaultRootId : '',
                                clearOnLoad : true,
                                nodeParam : 'id',
                                fields: [
                                    {
                                        name: 'id'
                                    },
                                    {
                                        name: 'parentId'
                                    },
                                    {
                                        name: 'sort',
                                        type: 'int'
                                    },
                                    {
                                        name: 'iconCls'
                                    },
                                    {
                                        name: 'leaf',
                                        type: 'boolean'
                                    },
                                    {
                                        name: 'type'
                                    },
                                    {
                                        name: 'resource'
                                    },
                                    {
                                        name: 'component'
                                    },
                                    {
                                        name: 'text'
                                    },
                                    {
                                        name: 'needCheck',
                                        type: 'boolean'
                                    },
                                    {
                                        name: 'checked',
                                        type: 'boolean'
                                    }
                                ],
                                 proxy:
                                    {
                                        type: 'memory',
                                        data: result.rows,
                                        reader:{
                                            type: 'json'
                                        }
                                    }
                                });

                    me.treePanel.bindStore(myStore);
                    myStore.reload();
                    me.treePanel.expandAll();
                    Ext.defer(function()
                         {
                             me.treePanel.expandAll();
                          }, 1000);
                    }
                });
    }

});